-- Sample Application Table
DROP TABLE PRODUCT;
DROP TABLE CATEGORY;
DROP TABLE SECURITY_USERS;

CREATE TABLE CATEGORY(CATEGORY_NO VARCHAR2(16) NOT NULL,CATEGORY_NAME VARCHAR2(50) NOT NULL,CATEGORY_DESC VARCHAR2(100),USE_YN CHAR(1),REG_ID VARCHAR2(20),REG_DATE DATE,MODIFY_ID VARCHAR2(20),MODIFY_DATE DATE,CONSTRAINT PK_CATEGORY PRIMARY KEY(CATEGORY_NO));
CREATE TABLE PRODUCT(PROD_NO VARCHAR2(16) NOT NULL,PROD_NAME VARCHAR2(100) NOT NULL,SELLER_ID VARCHAR2(20),CATEGORY_NO VARCHAR2(16),PROD_DETAIL VARCHAR2(200),MANUFACTURE_DAY VARCHAR2(8),AS_YN CHAR(1),SELL_QUANTITY NUMERIC(5),SELL_AMOUNT NUMERIC(10),IMAGE_FILE VARCHAR2(100),REG_DATE DATE,CONSTRAINT PK_PRODUCT PRIMARY KEY(PROD_NO),CONSTRAINT FK_PRODUCT FOREIGN KEY(CATEGORY_NO) REFERENCES CATEGORY(CATEGORY_NO));
CREATE TABLE GROUPS(GROUP_ID VARCHAR2(20) NOT NULL,GROUP_NAME VARCHAR2(50) NOT NULL,CREATE_DATE VARCHAR2(8),MODIFY_DATE VARCHAR2(8),CONSTRAINT PK_GROUPS PRIMARY KEY(GROUP_ID));
CREATE TABLE GROUPS_HIERARCHY(PARENT_GROUP VARCHAR2(50) NOT NULL,CHILD_GROUP VARCHAR2(50) NOT NULL,CREATE_DATE VARCHAR2(8),MODIFY_DATE VARCHAR2(8),CONSTRAINT PK_GROUPS_HIERARCHY PRIMARY KEY(PARENT_GROUP,CHILD_GROUP),CONSTRAINT FK_GROUPS1 FOREIGN KEY(PARENT_GROUP) REFERENCES GROUPS(GROUP_ID),CONSTRAINT FK_GROUPS2 FOREIGN KEY(CHILD_GROUP) REFERENCES GROUPS(GROUP_ID));
CREATE TABLE USERS(USER_ID VARCHAR2(20) NOT NULL,USER_NAME VARCHAR2(50) NOT NULL,PASSWORD VARCHAR2(50) NOT NULL,ENABLED CHAR(1),CREATE_DATE VARCHAR2(8),MODIFY_DATE VARCHAR2(8),CONSTRAINT PK_USERS PRIMARY KEY(USER_ID));
CREATE TABLE GROUPS_USERS(GROUP_ID VARCHAR2(20) NOT NULL,USER_ID VARCHAR2(20) NOT NULL,CREATE_DATE VARCHAR2(8),MODIFY_DATE VARCHAR2(8),CONSTRAINT PK_GROUPS_USERS PRIMARY KEY(GROUP_ID,USER_ID),CONSTRAINT FK_GROUPS_USERS_GROUP_ID FOREIGN KEY(GROUP_ID) REFERENCES GROUPS(GROUP_ID),CONSTRAINT FK_GROUPS_USERS_USER_ID FOREIGN KEY(USER_ID) REFERENCES USERS(USER_ID));
CREATE TABLE ROLES(ROLE_ID VARCHAR2(50) NOT NULL,ROLE_NAME VARCHAR2(50),DESCRIPTION VARCHAR2(100),CREATE_DATE VARCHAR2(8),MODIFY_DATE VARCHAR2(8),CONSTRAINT PK_ROLES PRIMARY KEY(ROLE_ID));
CREATE TABLE ROLES_HIERARCHY(PARENT_ROLE VARCHAR2(50) NOT NULL,CHILD_ROLE VARCHAR2(50) NOT NULL,CREATE_DATE VARCHAR2(8),MODIFY_DATE VARCHAR2(8),CONSTRAINT PK_ROLES_HIERARCHY PRIMARY KEY(PARENT_ROLE,CHILD_ROLE),CONSTRAINT FK_ROLES1 FOREIGN KEY(PARENT_ROLE) REFERENCES ROLES(ROLE_ID),CONSTRAINT FK_ROLES2 FOREIGN KEY(CHILD_ROLE) REFERENCES ROLES(ROLE_ID));
CREATE TABLE SECURED_RESOURCES(RESOURCE_ID VARCHAR2(10) NOT NULL,SYSTEM_NAME VARCHAR2(15),RESOURCE_NAME VARCHAR2(50),RESOURCE_PATTERN VARCHAR2(300) NOT NULL,DESCRIPTION VARCHAR2(100),RESOURCE_TYPE VARCHAR2(10) NOT NULL,SORT_ORDER NUMERIC,CREATE_DATE VARCHAR2(8),MODIFY_DATE VARCHAR2(8),CONSTRAINT PK_RECURED_RESOURCES PRIMARY KEY(RESOURCE_ID));
CREATE TABLE SECURED_RESOURCES_ROLES(RESOURCE_ID VARCHAR2(10) NOT NULL,ROLE_ID VARCHAR2(50) NOT NULL,CREATE_DATE VARCHAR2(8),MODIFY_DATE VARCHAR2(8),CONSTRAINT PK_SECURED_RESOURCES_ROLES PRIMARY KEY(RESOURCE_ID,ROLE_ID),CONSTRAINT FK_SECURED_RESOURCES FOREIGN KEY(RESOURCE_ID) REFERENCES SECURED_RESOURCES(RESOURCE_ID),CONSTRAINT FK_ROLES4 FOREIGN KEY(ROLE_ID) REFERENCES ROLES(ROLE_ID));
CREATE TABLE AUTHORITIES(ROLE_ID VARCHAR2(50) NOT NULL,SUBJECT_ID VARCHAR2(20) NOT NULL,TYPE VARCHAR2(2) NOT NULL,CREATE_DATE VARCHAR2(8),MODIFY_DATE VARCHAR2(8),CONSTRAINT PK_AUTHORITIES PRIMARY KEY(ROLE_ID,SUBJECT_ID),CONSTRAINT FK_ROLES3 FOREIGN KEY(ROLE_ID) REFERENCES ROLES(ROLE_ID));
CREATE TABLE RESTRICTED_TIMES(TIME_ID VARCHAR2(10) NOT NULL,SYSTEM_NAME VARCHAR2(15),TIME_TYPE VARCHAR2(10) NOT NULL,START_DATE VARCHAR2(8),START_TIME VARCHAR2(6) NOT NULL,END_DATE VARCHAR2(8),END_TIME VARCHAR2(6) NOT NULL,DESCRIPTION VARCHAR2(100),CONSTRAINT PK_RESTRICTED_TIMES PRIMARY KEY(TIME_ID));
CREATE TABLE RESTRICTED_TIMES_RESOURCES(TIME_ID VARCHAR2(10) NOT NULL,RESOURCE_ID VARCHAR2(10) NOT NULL,CONSTRAINT PK_RESTRICTED_TIMES_RESOURCES PRIMARY KEY(TIME_ID,RESOURCE_ID),CONSTRAINT FK_TIMES_RESOURCES_TIME_ID FOREIGN KEY(TIME_ID) REFERENCES RESTRICTED_TIMES(TIME_ID),CONSTRAINT FK_TIMES_RESOURCES_RESOURCE_ID FOREIGN KEY(RESOURCE_ID) REFERENCES SECURED_RESOURCES(RESOURCE_ID));
CREATE TABLE TIMES_RESOURCES_EXCLUSION(TIME_ID VARCHAR2(10) NOT NULL,RESOURCE_ID VARCHAR2(10) NOT NULL,ROLE_ID VARCHAR2(50) NOT NULL,CONSTRAINT PK_EXCLUSION PRIMARY KEY(TIME_ID,RESOURCE_ID,ROLE_ID),CONSTRAINT FK_EXCLUSION_TIME_ID FOREIGN KEY(TIME_ID) REFERENCES RESTRICTED_TIMES(TIME_ID),CONSTRAINT FK_EXCLUSION_RESOURCE_ID FOREIGN KEY(RESOURCE_ID) REFERENCES SECURED_RESOURCES(RESOURCE_ID),CONSTRAINT FK_EXCLUSION_ROLE_ID FOREIGN KEY(ROLE_ID) REFERENCES ROLES(ROLE_ID));
CREATE TABLE RESTRICTED_TIMES_ROLES(TIME_ID VARCHAR2(10) NOT NULL,ROLE_ID VARCHAR2(50) NOT NULL,CONSTRAINT PK_RESTRICTED_TIMES_ROLES PRIMARY KEY(TIME_ID,ROLE_ID),CONSTRAINT FK_TIMES_ROLES_TIME_ID FOREIGN KEY(TIME_ID) REFERENCES RESTRICTED_TIMES(TIME_ID),CONSTRAINT FK_TIMES_ROLES_ROLE_ID FOREIGN KEY(ROLE_ID) REFERENCES ROLES(ROLE_ID));
CREATE TABLE VIEW_RESOURCES(VIEW_RESOURCE_ID VARCHAR2(50) NOT NULL,SYSTEM_NAME VARCHAR2(15),CATEGORY VARCHAR2(255),VIEW_NAME VARCHAR2(50) NOT NULL,DESCRIPTION VARCHAR2(255) NOT NULL,VIEW_INFO VARCHAR2(255),VIEW_TYPE VARCHAR2(10) NOT NULL,VISIBLE VARCHAR2(1) NOT NULL,CONSTRAINT PK_VIEW_RESOURCES PRIMARY KEY(VIEW_RESOURCE_ID));
CREATE TABLE VIEW_RESOURCES_MAPPING(VIEW_RESOURCE_ID VARCHAR2(50) NOT NULL,REF_ID VARCHAR2(50) NOT NULL,MASK INTEGER NOT NULL,PERMISSIONS VARCHAR2(255) NOT NULL,REF_TYPE VARCHAR2(10) NOT NULL,CONSTRAINT PK_VIEW_RESOURCES_MAPPING PRIMARY KEY(VIEW_RESOURCE_ID,REF_ID),CONSTRAINT FK_MAPPING_VIEW_RESOURCE_ID FOREIGN KEY(VIEW_RESOURCE_ID) REFERENCES VIEW_RESOURCES(VIEW_RESOURCE_ID));
CREATE TABLE VIEW_HIERARCHY(PARENT_VIEW VARCHAR2(50) NOT NULL,CHILD_VIEW VARCHAR2(50) NOT NULL,CREATE_DATE VARCHAR2(8),MODIFY_DATE VARCHAR2(8),CONSTRAINT PK_VIEW_HIERARCHY PRIMARY KEY(PARENT_VIEW,CHILD_VIEW),CONSTRAINT FK_VIEW1 FOREIGN KEY(PARENT_VIEW) REFERENCES VIEW_RESOURCES(VIEW_RESOURCE_ID),CONSTRAINT FK_VIEW2 FOREIGN KEY(CHILD_VIEW) REFERENCES VIEW_RESOURCES(VIEW_RESOURCE_ID));
CREATE TABLE IDS(TABLE_NAME VARCHAR2(16) NOT NULL PRIMARY KEY,NEXT_ID NUMERIC(30) NOT NULL);
CREATE TABLE CANDIDATE_SECURED_RESOURCES(CANDIDATE_RESOURCE_ID VARCHAR2(20) NOT NULL,SYSTEM_NAME VARCHAR2(15),BEANID VARCHAR2(128),PACKAGES VARCHAR2(128) NOT NULL,CLASSES VARCHAR2(128),METHOD VARCHAR2(128),PARAMETER VARCHAR2(128),REQUESTMAPPING VARCHAR2(128),POINTCUT VARCHAR2(128),CANDIDATE_RESOURCE_TYPE VARCHAR2(10) NOT NULL,CONSTRAINT CANDIDATE_SECURED_RESOURCES_PK PRIMARY KEY(CANDIDATE_RESOURCE_ID));
CREATE TABLE SECURITY_USERS(USER_ID VARCHAR2(20) NOT NULL,USER_NAME VARCHAR2(50) NOT NULL,PASSWORD VARCHAR2(32) NOT NULL,ENABLED INTEGER,AGE NUMERIC(3),CELL_PHONE VARCHAR2(14),ADDR VARCHAR2(100),EMAIL VARCHAR2(50),CREATE_DATE VARCHAR2(8),MODIFY_DATE VARCHAR2(8),CONSTRAINT PK_SECURITY_USERS PRIMARY KEY(USER_ID));
